def generate_random_timestamps(start_date, end_date):
    start_timestamp = datetime.strptime(start_date, "%Y")
    end_timestamp = datetime.strptime(end_date, "%Y")

    random_timestamp = start_timestamp + \
        timedelta(seconds=random.randint(0, int((end_timestamp - start_timestamp).total_seconds())))
    return random_timestamp

